/* The USB module requires a GCLK_USB of 48 MHz ~ 0.25% clock
 * for low speed and full speed operation. */
#if (CONF_GCLK_USB_FREQUENCY > (48000000 + 48000000 / 400)) || (CONF_GCLK_USB_FREQUENCY < (48000000 - 48000000 / 400))
#warning USB clock should be 48MHz ~ 0.25% clock, check your configuration!
#endif

void USB_DEVICE_INSTANCE_CLOCK_init(void)
{

	hri_gclk_write_PCHCTRL_reg(GCLK, USB_GCLK_ID, CONF_GCLK_USB_SRC | GCLK_PCHCTRL_CHEN);
	hri_mclk_set_AHBMASK_USB_bit(MCLK);
	hri_mclk_set_APBBMASK_USB_bit(MCLK);
}

void USB_DEVICE_INSTANCE_init(void)
{
	USB_DEVICE_INSTANCE_CLOCK_init();
	usb_d_init();
	USB_DEVICE_INSTANCE_PORT_init();
}

void system_init(void)
{
	init_mcu();

	USB_DEVICE_INSTANCE_init();
	EXTERNAL_IRQ_0_init();

	CALENDER_INTERFACE_init();

	SPI_0_init();

	IO_BUS_init();

	PWM_0_init();

	PWM_1_init();

	
	gpio_set_pin_direction(LED0, GPIO_DIRECTION_OUT);
	gpio_set_pin_function(LED0, GPIO_PIN_FUNCTION_OFF);
	gpio_set_pin_direction(PB04, GPIO_DIRECTION_OUT);
	gpio_set_pin_function(PB04, GPIO_PIN_FUNCTION_OFF);
	gpio_set_pin_direction(PB05, GPIO_DIRECTION_OUT);
	gpio_set_pin_function(PB05, GPIO_PIN_FUNCTION_OFF);
	gpio_set_pin_level(PB04,0);
	gpio_set_pin_level(PB05,0);
	gpio_set_pin_level(LED0,1);
	
}
